cd ~ && nix-shell -p nodejs expect --run 'expect -c "
set timeout 60
set max_retries 3
set retry_count 0

proc retry_spawn {} {
  global retry_count max_retries
  incr retry_count
  if {$retry_count > $max_retries} {
    send_user \"Max retries ($max_retries) exceeded\\n\"
    exit 1
  }
  send_user \"Retry attempt $retry_count of $max_retries\\n\"
  spawn npx @anthropic-ai/claude-code --dangerously-skip-permissions \"Connect to SSH server a@192.168.50.6 with password LizardState!!777. Use expect and openssh from nix-shell. Establish interactive session and validate connection with hostname, whoami, pwd commands.\"
}

spawn npx @anthropic-ai/claude-code --dangerously-skip-permissions \"Connect to SSH server a@192.168.50.6 with password LizardState!!777. Use expect and openssh from nix-shell. Establish interactive session and validate connection with hostname, whoami, pwd commands.\"

expect {
  -re {.*bypass permissions.*} {
    send \"2\r\"
    exp_continue
  }
  -re {.*connected.*|.*ssh.*|.*Last login.*|.*successfully.*} {
    exit 0
  }
  -re {Connection refused|Connection closed|Connection reset} {
    send_user \"Connection error: $expect_out(buffer)\\n\"
    retry_spawn
    exp_continue
  }
  eof {
    send_user \"Unexpected EOF encountered\\n\"
    if {$retry_count < $max_retries} {
      retry_spawn
      exp_continue
    } else {
      exit 1
    }
  }
  timeout {
    send_user \"Timeout after $timeout seconds\\n\"
    if {$retry_count < $max_retries} {
      retry_spawn
      exp_continue
    } else {
      exit 1
    }
  }
}
"'

