input.command:
cd ~ && nix-shell -p nodejs expect --run 'expect -c "
spawn npx @anthropic-ai/claude-code --dangerously-skip-permissions \"Scan the 192.168.50.0/24 network for all SSH services. Use nix-shell -p nmap to scan for open port 22 and alternative SSH ports (2222, 22222). List all discovered SSH services except the local machine at 192.168.50.33. Show IP, hostname, SSH version for each.\"
expect {
  -re {.*bypass permissions.*} {
    send \"2\r\"
    exp_continue
  }
  -re {.*created.*|.*written.*|.*saved.*|.*found.*|.*SSH.*} {
    exit 0
  }
  timeout {
    exit 1
  }
}
"'

