#!/usr/bin/env python3
"""Script to print the first 10 prime numbers."""

def is_prime(n):
    """Check if a number is prime."""
    if n < 2:
        return False
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True

def get_first_n_primes(n):
    """Get the first n prime numbers."""
    primes = []
    num = 2  # Start checking from 2 (the first prime)

    while len(primes) < n:
        if is_prime(num):
            primes.append(num)
        num += 1

    return primes

# Main execution
if __name__ == "__main__":
    # Get the first 10 prime numbers
    first_10_primes = get_first_n_primes(10)

    # Print the results
    print("The first 10 prime numbers are:")
    for i, prime in enumerate(first_10_primes, 1):
        print(f"{i}. {prime}")

