input.command:
ghostty -e bash -c 'cd ~ && nix-shell -p nodejs expect --run "expect -c \"
set timeout 30
spawn npx @anthropic-ai/claude-code --dangerously-skip-permissions {Create a file at /tmp/expect_test.txt with: Test successful from expect agent}
expect {
  -re {.*Choose an option.*} { send \\"2\\r\\"; exp_continue }
  -re {.*Continue anyway.*} { send \\"y\\r\\"; exp_continue }
  timeout { puts \\"Timeout reached\\"; exit 1 }
  eof
}
\""' &

cd ~ && nix-shell -p nodejs expect --run 'expect -c "
spawn npx @anthropic-ai/claude-code --dangerously-skip-permissions \"What is 3+3?\"
expect {
  -re {.*bypass permissions.*} { 
    send \"2\r\"
    exp_continue 
  }
  -re {.*6.*} {
    puts \"Got answer: 6\"
    exit 0
  }
  timeout {
    puts \"Timeout reached\"
    exit 1
  }
}
"'

cd ~ && nix-shell -p nodejs expect --run 'expect -c "
spawn npx @anthropic-ai/claude-code --dangerously-skip-permissions \"Create a file at /tmp/expect_automated.txt with the content: Automated test successful at $(date)\"
expect {
  -re {.*bypass permissions.*} { 
    send \"2\r\"
    exp_continue 
  }
  -re {.*created.*|.*written.*|.*saved.*} {
    puts \"File operation appears complete\"
    exit 0
  }
  timeout {
    puts \"Timeout after 15 seconds\"
    exit 1
  }
}
"' && ls -la /tmp/expect_automated.txt 2>/dev/null
