#!/bin/bash

# SSH connection script with automatic password handling
nix-shell -p expect openssh --run 'expect -c "
set timeout 30
spawn ssh a@192.168.50.6
expect {
  \"Are you sure you want to continue connecting\" {
    send \"yes\r\"
    exp_continue
  }
  \"password:\" {
    send \"LizardState!!777\r\"
  }
  timeout {
    puts \"Connection timeout\"
    exit 1
  }
  eof {
    puts \"Connection closed unexpectedly\"
    exit 1
  }
}
expect {
  \"$\" {
    send \"echo SSH connection successful\r\"
    send \"hostname\r\"
    send \"whoami\r\"
    send \"ls -la\r\"
    interact
  }
  \"Permission denied\" {
    puts \"Authentication failed\"
    exit 1
  }
  timeout {
    puts \"Login timeout\"
    exit 1
  }
}
"'

