#!/bin/bash
# Launch 3 parallel agents with different tasks
for i in 1 2 3; do
  (cd ~ && nix-shell -p nodejs expect --run "expect -c \"
    set timeout 120
    spawn npx @anthropic-ai/claude-code --dangerously-skip-permissions \\\"Create a Python file agent_$i.py that prints 'Hello from parallel agent $i'\\\"
    expect {
      -re {.*bypass permissions.*} { 
        send \\\"2\\\r\\\"
      }
    }
    expect {
      -re {.*Write.*} {
        send_user \\\"\\n=== Agent $i completed ===\\n\\\"
        exit 0
      }
      timeout {
        send_user \\\"\\n=== Agent $i timeout ===\\n\\\"
        exit 1
      }
    }
  \"") &
done

echo "=== Launched 3 parallel agents ==="
wait  # Wait for all background processes
echo "=== All agents finished ==="

# Check results
sleep 5
echo "=== Checking created files ==="
ls -la ~/agent_*.py 2>/dev/null | wc -l && echo "files created"

