     1→{
     2→  inputs,
     3→  ...
     4→}:
     5→let
     6→  # Package configuration - sets up package system with proper overlays
     7→  # Most users won't need to modify this section
     8→  pkgs = import inputs.hydenix.inputs.hydenix-nixpkgs {
     9→    inherit (inputs.hydenix.lib) system;
    10→    config.allowUnfree = true;
    11→    overlays = [
    12→      inputs.hydenix.lib.overlays
    13→      (final: prev: {
    14→        userPkgs = import inputs.nixpkgs {
    15→          config.allowUnfree = true;
    16→        };
    17→      })
    18→    ];
    19→  };
    20→in
    21→{
    22→  nixpkgs.pkgs = pkgs; # Set pkgs for hydenix globally
    23→
    24→  imports = [
    25→    # hydenix inputs - Required modules, don't modify unless you know what you're doing
    26→    inputs.hydenix.inputs.home-manager.nixosModules.home-manager
    27→    inputs.hydenix.lib.nixOsModules
    28→    ./modules/system # Your custom system modules
    29→    ./modules/system/update-alias.nix # Update! command for quick commits and rebuilds
    30→    ./hardware-config.nix # Hardware configuration wrapper
    31→
    32→    # Hardware Configuration - Uncomment lines that match your hardware
    33→    # Run `lshw -short` or `lspci` to identify your hardware
    34→
    35→    # GPU Configuration (choose one):
    36→    # inputs.hydenix.inputs.nixos-hardware.nixosModules.common-gpu-nvidia # NVIDIA
    37→    # inputs.hydenix.inputs.nixos-hardware.nixosModules.common-gpu-amd # AMD
    38→
    39→    inputs.hydenix.inputs.nixos-hardware.nixosModules.gpd-pocket-3
    40→
    41→    # CPU Configuration (choose one):
    42→    # inputs.hydenix.inputs.nixos-hardware.nixosModules.common-cpu-amd # AMD CPUs
    43→    # inputs.hydenix.inputs.nixos-hardware.nixosModules.common-cpu-intel # Intel CPUs
    44→
    45→    # Additional Hardware Modules - Uncomment based on your system type:
    46→    # inputs.hydenix.inputs.nixos-hardware.nixosModules.common-hidpi # High-DPI displays
    47→    # inputs.hydenix.inputs.nixos-hardware.nixosModules.common-pc-laptop # Laptops
    48→    # inputs.hydenix.inputs.nixos-hardware.nixosModules.common-pc-ssd # SSD storage
    49→  ];
    50→
    51→  # If enabling NVIDIA, you will be prompted to configure hardware.nvidia
    52→
