    54→  environment.sessionVariables = {
    55→    TERMINAL = "ghostty";
    56→  };
    57→  # hardware.nvidia = {
    58→  #   open = true; # For newer cards, you may want open drivers
    59→  #   prime = { # For hybrid graphics (laptops), configure PRIME:
    60→  #     amdBusId = "PCI:0:2:0"; # Run `lspci | grep VGA` to get correct bus IDs
    61→  #     intelBusId = "PCI:0:2:0"; # if you have intel graphics
    62→  #     nvidiaBusId = "PCI:1:0:0";
    63→  #     offload.enable = false; # Or disable PRIME offloading if you don't care
    64→  #   };
    65→  # };
    66→
    67→  # Home Manager Configuration - manages user-specific configurations (dotfiles, themes, etc.)
    68→  home-manager = {
    69→    backupFileExtension = "backup";
    70→    useGlobalPkgs = true;
    71→    useUserPackages = true;
    72→    extraSpecialArgs = { inherit inputs; };
    73→    # User Configuration - REQUIRED: Change "hydenix" to your actual username
    74→    # This must match the username you define in users.users below
    75→    users."a" =
    76→      { ... }:
    77→      {
    78→        imports = [
    79→          inputs.hydenix.lib.homeModules
    80→          inputs.nix-index-database.homeModules.nix-index # Command-not-found and comma tool support
    81→          ./modules/hm # Your custom home-manager modules (configure hydenix.hm here!)
    82→        ];
    83→      };
    84→  };
    85→
    86→  # User Account Setup - REQUIRED: Change "hydenix" to your desired username (must match above)
    87→  users.users.a = {
    88→    isNormalUser = true;
    89→    initialPassword = "a"; # SECURITY: Change this password after first login with `passwd`
    90→    extraGroups = [
    91→      "wheel"
    92→      "networkmanager"
    93→      "video"
    94→      "input"  # Required for gesture recognition
