{ config, lib, pkgs, ... }:

with lib;

let
  cfg = config.custom.system.power.battery;
in
{
  options.custom.system.power.battery = {
    enable = mkEnableOption "battery optimization for GPD Pocket 3";

    tlp = {
      enable = mkOption {
        type = types.bool;
        default = true;
        description = "Enable TLP advanced power management";
      };

      chargeThresholds = {
        startThreshold = mkOption {
          type = types.int;
          default = 20;
          description = "Battery charge start threshold (%)";
        };

        stopThreshold = mkOption {
          type = types.int;
          default = 80;
          description = "Battery charge stop threshold (%)";
        };
      };

      powerProfiles = {
        performance = mkOption {
          type = types.bool;
          default = true;
          description = "Enable performance power profile";
        };

        balanced = mkOption {
          type = types.bool;
          default = true;
          description = "Enable balanced power profile";
        };

        powerSave = mkOption {
          type = types.bool;
          default = true;
          description = "Enable power save profile";
        };
      };

      cpuGovernor = {
        onAC = mkOption {
          type = types.enum [ "performance" "powersave" "ondemand" "conservative" "schedutil" ];
          default = "performance";
          description = "CPU governor when on AC power";
        };

        onBattery = mkOption {
          type = types.enum [ "performance" "powersave" "ondemand" "conservative" "schedutil" ];
          default = "powersave";
          description = "CPU governor when on battery";
        };
      };

      intelCpuSettings = {
        enablePstates = mkOption {
          type = types.bool;
          default = true;
          description = "Enable Intel P-states for i3-1125G4";
        };

        enableTurboBoost = mkOption {
          type = types.bool;
          default = true;
          description = "Enable Intel Turbo Boost on AC";
        };

        disableTurboOnBattery = mkOption {
          type = types.bool;
          default = true;
          description = "Disable Turbo Boost on battery to save power";
        };

        minFreqPercent = mkOption {
          type = types.int;
          default = 0;
          description = "Minimum CPU frequency percentage";
        };

        maxFreqPercentAC = mkOption {
          type = types.int;
          default = 100;
          description = "Maximum CPU frequency percentage on AC";
        };

        maxFreqPercentBattery = mkOption {
          type = types.int;
          default = 80;
          description = "Maximum CPU frequency percentage on battery";
        };
      };
    };

    monitoring = {
      enable = mkOption {
        type = types.bool;
        default = true;
        description = "Enable battery health monitoring";
      };

      enableUpower = mkOption {
        type = types.bool;
        default = true;
        description = "Enable UPower for battery statistics";
      };

      enableAcpi = mkOption {
        type = types.bool;
        default = true;
        description = "Enable ACPI tools for battery info";
      };

      enablePowerTop = mkOption {
        type = types.bool;
        default = false;
        description = "Enable PowerTOP for power analysis";
      };

      alertThresholds = {
        lowBattery = mkOption {
          type = types.int;
          default = 15;
          description = "Low battery warning threshold (%)";
        };

        criticalBattery = mkOption {
          type = types.int;
          default = 5;
          description = "Critical battery threshold (%)";
        };
      };
    };

    additionalOptimizations = {
      enableAutoCpufreq = mkOption {
        type = types.bool;
        default = false;
        description = "Enable auto-cpufreq (alternative to TLP, mutually exclusive)";
      };

      disableWakeOnLan = mkOption {
        type = types.bool;
        default = true;
        description = "Disable Wake-on-LAN to save power";
      };

      enableWifiPowerSave = mkOption {
        type = types.bool;
        default = true;
        description = "Enable WiFi power saving";
      };

      reduceSwappiness = mkOption {
        type = types.bool;
        default = true;
        description = "Reduce swappiness to minimize disk usage";
      };

      enableKernelPowerSaving = mkOption {
        type = types.bool;
        default = true;
        description = "Enable various kernel power saving features";
      };
    };
  };

  config = mkIf cfg.enable (mkMerge [
    # Core TLP configuration
    (mkIf cfg.tlp.enable {
      services.tlp = {
        enable = true;
        settings = {
          # Battery charge thresholds
          START_CHARGE_THRESH_BAT0 = cfg.tlp.chargeThresholds.startThreshold;
          STOP_CHARGE_THRESH_BAT0 = cfg.tlp.chargeThresholds.stopThreshold;

          # CPU scaling governor
          CPU_SCALING_GOVERNOR_ON_AC = cfg.tlp.cpuGovernor.onAC;
          CPU_SCALING_GOVERNOR_ON_BAT = cfg.tlp.cpuGovernor.onBattery;

          # REMOVED PROBLEMATIC FREQUENCY SETTINGS
          # CPU frequency scaling is now handled by governors only
          # This prevents invalid frequency values from causing boot failures

          # Intel CPU energy performance preferences
          CPU_ENERGY_PERF_POLICY_ON_AC = "performance";
          CPU_ENERGY_PERF_POLICY_ON_BAT = "power";

          # Intel Turbo Boost
          CPU_BOOST_ON_AC = if cfg.tlp.intelCpuSettings.enableTurboBoost then 1 else 0;
          CPU_BOOST_ON_BAT = if cfg.tlp.intelCpuSettings.disableTurboOnBattery then 0 else 1;

          # Intel HWP (Hardware P-states) for Tiger Lake
          CPU_HWP_DYN_BOOST_ON_AC = 1;
          CPU_HWP_DYN_BOOST_ON_BAT = 0;

          # Platform profile for modern Intel CPUs
          PLATFORM_PROFILE_ON_AC = "performance";
          PLATFORM_PROFILE_ON_BAT = "low-power";

          # PCI Express power management
          PCIE_ASPM_ON_AC = "default";
          PCIE_ASPM_ON_BAT = "powersupersave";

          # WiFi power saving (Intel AX201 common in Tiger Lake)
          WIFI_PWR_ON_AC = "off";
          WIFI_PWR_ON_BAT = "on";

          # Audio power saving
          SOUND_POWER_SAVE_ON_AC = 0;
          SOUND_POWER_SAVE_ON_BAT = 1;
          SOUND_POWER_SAVE_CONTROLLER = "Y";

          # USB power management
          USB_AUTOSUSPEND = 1;
          USB_EXCLUDE_AUDIO = 1;
          USB_EXCLUDE_BTUSB = 0;
          USB_EXCLUDE_PHONE = 0;
          USB_EXCLUDE_PRINTER = 1;
          USB_EXCLUDE_WWAN = 0;

          # Runtime power management
          RUNTIME_PM_ON_AC = "on";
          RUNTIME_PM_ON_BAT = "auto";

          # SATA link power management
          SATA_LINKPWR_ON_AC = "med_power_with_dipm";
          SATA_LINKPWR_ON_BAT = "min_power";

          # Disk power management
          DISK_APM_LEVEL_ON_AC = "254";
          DISK_APM_LEVEL_ON_BAT = "128";
          DISK_SPINDOWN_TIMEOUT_ON_AC = "0";
          DISK_SPINDOWN_TIMEOUT_ON_BAT = "12";

          # Radios (Bluetooth, WiFi, WWAN)
          RESTORE_DEVICE_STATE_ON_STARTUP = 0;
          DEVICES_TO_DISABLE_ON_STARTUP = "";
          DEVICES_TO_ENABLE_ON_STARTUP = "wifi bluetooth";
          DEVICES_TO_DISABLE_ON_SHUTDOWN = "";
          DEVICES_TO_ENABLE_ON_SHUTDOWN = "";
          DEVICES_TO_ENABLE_ON_AC = "";
          DEVICES_TO_DISABLE_ON_BAT = "";
          DEVICES_TO_DISABLE_ON_BAT_NOT_IN_USE = "";

          # Battery care settings
          NATACPI_ENABLE = 1;
          TPACPI_ENABLE = 1;
          TPSMAPI_ENABLE = 1;
        };
      };

      # Conflict resolution: disable other power management if TLP is enabled
      powerManagement.powertop.enable = mkForce false;
      services.auto-cpufreq.enable = mkForce false;
      services.power-profiles-daemon.enable = mkForce false;
    })

    # Auto-cpufreq as alternative to TLP
    (mkIf (cfg.additionalOptimizations.enableAutoCpufreq && !cfg.tlp.enable) {
      services.auto-cpufreq = {
        enable = true;
        settings = {
          battery = {
            governor = cfg.tlp.cpuGovernor.onBattery;
            turbo = if cfg.tlp.intelCpuSettings.disableTurboOnBattery then "never" else "auto";
          };
          charger = {
            governor = cfg.tlp.cpuGovernor.onAC;
            turbo = if cfg.tlp.intelCpuSettings.enableTurboBoost then "auto" else "never";
          };
        };
      };

      # Disable conflicting services
      services.tlp.enable = mkForce false;
      services.power-profiles-daemon.enable = mkForce false;
    })

    # Battery monitoring tools
    (mkIf cfg.monitoring.enable {
      # UPower for battery statistics
      services.upower = mkIf cfg.monitoring.enableUpower {
        enable = true;
        percentageLow = cfg.monitoring.alertThresholds.lowBattery;
        percentageCritical = cfg.monitoring.alertThresholds.criticalBattery;
        percentageAction = cfg.monitoring.alertThresholds.criticalBattery;
        criticalPowerAction = "Suspend";
        allowRiskyCriticalPowerAction = true;
      };

      # System packages for monitoring
      environment.systemPackages = with pkgs; [
        (mkIf cfg.monitoring.enableAcpi acpi)
        (mkIf cfg.monitoring.enablePowerTop powertop)
        (mkIf cfg.tlp.enable tlp)
        # Battery health monitoring tools
        upower
        lm_sensors
        cpufrequtils
        cpupower-gui
        # Intel-specific tools
        intel-gpu-tools
      ];
    })

    # Additional optimizations
    (mkIf cfg.additionalOptimizations.enableKernelPowerSaving {
      # Kernel parameters for power saving
      boot.kernelParams = [
        # Intel i915 graphics power saving
        "i915.enable_rc6=1"
        "i915.enable_fbc=1"
        "i915.lvds_downclock=1"
        "i915.semaphores=1"

        # ACPI and power management
        "acpi_osi=Linux"
        "acpi_backlight=vendor"

        # Disable unnecessary hardware
        "modprobe.blacklist=pcspkr"

        # NMI watchdog (saves power)
        "nmi_watchdog=0"

        # Enable ASPM (might be overridden by TLP)
        "pcie_aspm=force"
      ];

      # Kernel modules
      boot.kernelModules = [
        "acpi_cpufreq"  # CPU frequency scaling
        "cpufreq_powersave"
        "cpufreq_conservative"
        "cpufreq_ondemand"
      ];
    })

    # Network power optimizations
    (mkIf cfg.additionalOptimizations.enableWifiPowerSave {
      # WiFi power management using kernel modules
      boot.extraModprobeConfig = ''
        # Enable WiFi power saving
        options iwlwifi power_save=1
        options iwldvm force_cam=0
      '';
    })

    # Swappiness optimization
    (mkIf cfg.additionalOptimizations.reduceSwappiness {
      boot.kernel.sysctl = {
        "vm.swappiness" = 10;  # Reduce swap usage
        "vm.dirty_ratio" = 15;  # Reduce disk writes
        "vm.dirty_background_ratio" = 5;
      };
    })

    # Wake-on-LAN disable
    (mkIf cfg.additionalOptimizations.disableWakeOnLan {
      systemd.services.disable-wol = {
        description = "Disable Wake-on-LAN";
        after = [ "network.target" ];
        wantedBy = [ "multi-user.target" ];
        serviceConfig = {
          Type = "oneshot";
          RemainAfterExit = true;
          ExecStart = "${pkgs.ethtool}/bin/ethtool -s enp0s3 wol d || true";
        };
      };
    })

    # Systemd optimizations for better power management
    {
      systemd.settings = {
        Manager = {
          DefaultTimeoutStopSec = "30s";
          DefaultTimeoutStartSec = "30s";
        };
      };

      # Power button handling
      services.logind.settings = {
        Login = {
          HandlePowerKey = "suspend";
          PowerKeyIgnoreInhibited = "no";
        };
      };

      # Tmpfs optimizations to reduce disk writes
      fileSystems."/tmp" = {
        device = "tmpfs";
        fsType = "tmpfs";
        options = [ "defaults" "size=2G" "mode=1777" ];
      };
    }

    # Create battery monitoring scripts
    (mkIf cfg.monitoring.enable {
      environment.systemPackages = [
        (pkgs.writeShellScriptBin "battery-status" ''
          #!/bin/bash
          echo "=== Battery Status ==="
          ${pkgs.acpi}/bin/acpi -b
          echo ""
          echo "=== Power Profile ==="
          ${pkgs.tlp}/bin/tlp-stat -s 2>/dev/null || echo "TLP not running"
          echo ""
          echo "=== CPU Governor ==="
          cat /sys/devices/system/cpu/cpu0/cpufreq/scaling_governor 2>/dev/null || echo "N/A"
          echo ""
          echo "=== CPU Frequency ==="
          cat /proc/cpuinfo | grep "cpu MHz" | head -4
          echo ""
          echo "=== Battery Health ==="
          ${pkgs.upower}/bin/upower -i /org/freedesktop/UPower/devices/battery_BAT0 2>/dev/null || echo "Battery info not available"
        '')

        (pkgs.writeShellScriptBin "power-profile" ''
          #!/bin/bash
          case "$1" in
            performance)
              echo "Switching to performance mode..."
              ${optionalString cfg.tlp.enable "${pkgs.tlp}/bin/tlp ac"}
              ;;
            powersave)
              echo "Switching to power save mode..."
              ${optionalString cfg.tlp.enable "${pkgs.tlp}/bin/tlp bat"}
              ;;
            *)
              echo "Usage: power-profile [performance|powersave]"
              echo "Current status:"
              ${optionalString cfg.tlp.enable "${pkgs.tlp}/bin/tlp-stat -s"}
              ;;
          esac
        '')
      ];
    })
  ]);
}

{ config, lib, pkgs, ... }:

with lib;

let
  cfg = config.custom.system.hardware.monitoring;

  # Hardware monitoring script for GPD Pocket 3
  monitor-script = pkgs.writeShellScriptBin "gpd-hardware-monitor" ''
    #!/bin/bash

    # Hardware monitoring for GPD Pocket 3
    # Monitors temperatures, fan speeds, battery, and storage health

    set -euo pipefail

    # Configuration
    TEMP_HIGH_THRESHOLD=${toString cfg.alerts.temperatureHigh}
    BATTERY_LOW_THRESHOLD=${toString cfg.alerts.batteryLow}
    LOG_FILE="${cfg.logging.logFile}"
    LOG_LEVEL="${cfg.logging.level}"
    ALERT_METHOD="${cfg.alerts.method}"
    CHECK_INTERVAL=${toString cfg.checkInterval}

    # Ensure log directory exists
    mkdir -p "$(dirname "$LOG_FILE")"

    # Logging functions
    log_message() {
        local level="$1"
        local message="$2"
        local timestamp=$(${pkgs.coreutils}/bin/date '+%Y-%m-%d %H:%M:%S')

        case "$LOG_LEVEL" in
            "debug") echo "[$timestamp] [$level] $message" | ${pkgs.coreutils}/bin/tee -a "$LOG_FILE" ;;
            "info")
                if [[ "$level" != "DEBUG" ]]; then
                    echo "[$timestamp] [$level] $message" | ${pkgs.coreutils}/bin/tee -a "$LOG_FILE"
                fi ;;
            "warn")
                if [[ "$level" == "WARN" || "$level" == "ERROR" || "$level" == "CRITICAL" ]]; then
                    echo "[$timestamp] [$level] $message" | ${pkgs.coreutils}/bin/tee -a "$LOG_FILE"
                fi ;;
            "error")
                if [[ "$level" == "ERROR" || "$level" == "CRITICAL" ]]; then
                    echo "[$timestamp] [$level] $message" | ${pkgs.coreutils}/bin/tee -a "$LOG_FILE"
                fi ;;
        esac
    }

    # Alert notification function
    send_alert() {
        local severity="$1"
        local message="$2"

        log_message "$severity" "$message"

        case "$ALERT_METHOD" in
            "notify")
                if command -v ${pkgs.libnotify}/bin/notify-send >/dev/null 2>&1; then
                    ${pkgs.libnotify}/bin/notify-send -u critical "Hardware Alert" "$message"
                fi ;;
            "systemd")
                ${pkgs.systemd}/bin/systemd-cat -t "gpd-hardware-monitor" -p "$severity" echo "$message" ;;
            "log")
                # Already logged above
                ;;
            "all")
                if command -v ${pkgs.libnotify}/bin/notify-send >/dev/null 2>&1; then
                    ${pkgs.libnotify}/bin/notify-send -u critical "Hardware Alert" "$message"
                fi
                ${pkgs.systemd}/bin/systemd-cat -t "gpd-hardware-monitor" -p "$severity" echo "$message"
                ;;
        esac
    }

    # Temperature monitoring
    check_temperatures() {
        log_message "DEBUG" "Checking CPU/GPU temperatures"

        # Initialize sensors if available
        if command -v ${pkgs.lm_sensors}/bin/sensors-detect >/dev/null 2>&1; then
            ${pkgs.lm_sensors}/bin/sensors >/dev/null 2>&1 || true
        fi

        # Check CPU temperature via hwmon
        local cpu_temp=""
        local gpu_temp=""

        # Try different hwmon paths for CPU temperature
        for hwmon in /sys/class/hwmon/hwmon*; do
            if [[ -f "$hwmon/name" ]]; then
                local sensor_name=$(cat "$hwmon/name")
                case "$sensor_name" in
                    "coretemp"|"k10temp"|"zenpower")
                        if [[ -f "$hwmon/temp1_input" ]]; then
                            cpu_temp=$(($(cat "$hwmon/temp1_input") / 1000))
                            log_message "DEBUG" "CPU temperature: ''${cpu_temp}Â°C (sensor: $sensor_name)"
                            break
                        fi ;;
                esac
            fi
        done

        # Try ACPI thermal zones as fallback
        if [[ -z "$cpu_temp" ]]; then
            for thermal in /sys/class/thermal/thermal_zone*; do
                if [[ -f "$thermal/temp" ]]; then
                    local temp=$(($(cat "$thermal/temp") / 1000))
                    if [[ $temp -gt 0 && $temp -lt 200 ]]; then  # Reasonable temperature range
                        cpu_temp=$temp
                        log_message "DEBUG" "CPU temperature: ''${cpu_temp}Â°C (thermal zone)"
                        break
                    fi
                fi
            done
        fi

        # Check GPU temperature (Intel integrated graphics)
        for hwmon in /sys/class/hwmon/hwmon*; do
            if [[ -f "$hwmon/name" ]]; then
                local sensor_name=$(cat "$hwmon/name")
                if [[ "$sensor_name" == "i915" ]]; then
                    if [[ -f "$hwmon/temp1_input" ]]; then
                        gpu_temp=$(($(cat "$hwmon/temp1_input") / 1000))
                        log_message "DEBUG" "GPU temperature: ''${gpu_temp}Â°C"
                        break
                    fi
                fi
            fi
        done

        # Check temperature alerts
        if [[ -n "$cpu_temp" ]] && [[ $cpu_temp -ge $TEMP_HIGH_THRESHOLD ]]; then
            send_alert "CRITICAL" "CPU temperature critical: ''${cpu_temp}Â°C (threshold: ''${TEMP_HIGH_THRESHOLD}Â°C)"
        fi

        if [[ -n "$gpu_temp" ]] && [[ $gpu_temp -ge $TEMP_HIGH_THRESHOLD ]]; then
            send_alert "CRITICAL" "GPU temperature critical: ''${gpu_temp}Â°C (threshold: ''${TEMP_HIGH_THRESHOLD}Â°C)"
        fi

        # Return temperatures for status display
        echo "CPU_TEMP=$cpu_temp"
        echo "GPU_TEMP=$gpu_temp"
    }

    # Fan speed monitoring
    check_fan_speeds() {
        log_message "DEBUG" "Checking fan speeds"

        local fan_speed=""

        # Check for fan sensors in hwmon
        for hwmon in /sys/class/hwmon/hwmon*; do
            if [[ -f "$hwmon/fan1_input" ]]; then
                fan_speed=$(cat "$hwmon/fan1_input")
                log_message "DEBUG" "Fan speed: ''${fan_speed} RPM"
                break
            fi
        done

        # Check ACPI fan status as fallback
        if [[ -z "$fan_speed" ]] && [[ -d "/proc/acpi/fan" ]]; then
            for fan_dir in /proc/acpi/fan/*/; do
                if [[ -f "$fan_dir/state" ]]; then
                    local fan_state=$(cat "$fan_dir/state")
                    log_message "DEBUG" "Fan state: $fan_state"
                fi
            done
        fi

        echo "FAN_SPEED=$fan_speed"
    }

    # Battery health monitoring
    check_battery() {
        log_message "DEBUG" "Checking battery status"

        local battery_level=""
        local battery_status=""
        local battery_health=""

        # Check battery via sysfs
        for battery in /sys/class/power_supply/BAT*; do
            if [[ -d "$battery" ]]; then
                if [[ -f "$battery/capacity" ]]; then
                    battery_level=$(cat "$battery/capacity")
                fi
                if [[ -f "$battery/status" ]]; then
                    battery_status=$(cat "$battery/status")
                fi
                if [[ -f "$battery/health" ]]; then
                    battery_health=$(cat "$battery/health")
                fi
                break
            fi
        done

        # Try ACPI as fallback
        if [[ -z "$battery_level" ]] && command -v ${pkgs.acpi}/bin/acpi >/dev/null 2>&1; then
            local acpi_output=$(${pkgs.acpi}/bin/acpi -b 2>/dev/null || true)
            if [[ -n "$acpi_output" ]]; then
                battery_level=$(echo "$acpi_output" | ${pkgs.gnugrep}/bin/grep -oP '\d+(?=%)' | head -1)
                battery_status=$(echo "$acpi_output" | ${pkgs.gnugrep}/bin/grep -oP '(Charging|Discharging|Full|Unknown)' | head -1)
            fi
        fi

        log_message "DEBUG" "Battery: ''${battery_level}% (''${battery_status}) Health: ''${battery_health}"

        # Check battery alerts
        if [[ -n "$battery_level" ]] && [[ $battery_level -le $BATTERY_LOW_THRESHOLD ]] && [[ "$battery_status" == "Discharging" ]]; then
            send_alert "WARN" "Battery level low: ''${battery_level}% (threshold: ''${BATTERY_LOW_THRESHOLD}%)"
        fi

        echo "BATTERY_LEVEL=$battery_level"
        echo "BATTERY_STATUS=$battery_status"
        echo "BATTERY_HEALTH=$battery_health"
    }

    # Storage health monitoring
    check_storage() {
        log_message "DEBUG" "Checking storage health"

        # Get all block devices
        for device in $(${pkgs.util-linux}/bin/lsblk -dno NAME | ${pkgs.gnugrep}/bin/grep -E '^(sd|nvme|mmc)'); do
            local device_path="/dev/$device"

            # Skip if device doesn't exist
            [[ -b "$device_path" ]] || continue

            log_message "DEBUG" "Checking storage device: $device_path"

            # Check SMART status if available
            if command -v ${pkgs.smartmontools}/bin/smartctl >/dev/null 2>&1; then
                local smart_output=$(${pkgs.smartmontools}/bin/smartctl -H "$device_path" 2>/dev/null || true)
                if [[ -n "$smart_output" ]]; then
                    if echo "$smart_output" | ${pkgs.gnugrep}/bin/grep -q "PASSED"; then
                        log_message "DEBUG" "SMART status for $device: PASSED"
                    elif echo "$smart_output" | ${pkgs.gnugrep}/bin/grep -q "FAILED"; then
                        send_alert "CRITICAL" "SMART status for $device: FAILED"
                    fi
                fi

                # Check temperature for NVMe drives
                if [[ "$device" =~ ^nvme ]]; then
                    local nvme_temp=$(${pkgs.smartmontools}/bin/smartctl -a "$device_path" 2>/dev/null | \
                        ${pkgs.gnugrep}/bin/grep -oP 'Temperature:\s+\K\d+' || echo "")
                    if [[ -n "$nvme_temp" ]]; then
                        log_message "DEBUG" "NVMe temperature for $device: ''${nvme_temp}Â°C"
                        if [[ $nvme_temp -ge 70 ]]; then  # NVMe thermal throttling threshold
                            send_alert "WARN" "NVMe temperature high for $device: ''${nvme_temp}Â°C"
                        fi
                    fi
                fi
            fi
        done
    }

    # Main monitoring loop
    run_monitoring() {
        log_message "INFO" "Starting hardware monitoring for GPD Pocket 3"

        while true; do
            {
                check_temperatures
                check_fan_speeds
                check_battery
                check_storage
                echo "LAST_CHECK=$(${pkgs.coreutils}/bin/date '+%Y-%m-%d %H:%M:%S')"
            } > /tmp/gpd-hardware-status

            ${pkgs.coreutils}/bin/sleep "$CHECK_INTERVAL"
        done
    }

    # Handle script arguments
    case "''${1:-monitor}" in
        "monitor")
            run_monitoring ;;
        "status")
            if [[ -f /tmp/gpd-hardware-status ]]; then
                cat /tmp/gpd-hardware-status
            else
                echo "No monitoring data available"
                exit 1
            fi ;;
        "test")
            log_message "INFO" "Running hardware monitoring test"
            check_temperatures
            check_fan_speeds
            check_battery
            check_storage
            log_message "INFO" "Hardware monitoring test completed" ;;
        *)
            echo "Usage: $0 {monitor|status|test}"
            echo "  monitor - Start continuous monitoring (default)"
            echo "  status  - Show current hardware status"
            echo "  test    - Run one-time hardware check"
            exit 1 ;;
    esac
  '';

  # Status display script
  status-script = pkgs.writeShellScriptBin "gpd-hardware-status" ''
    #!/bin/bash

    # Display hardware status in human-readable format

    if [[ ! -f /tmp/gpd-hardware-status ]]; then
        echo "Hardware monitoring not running or no data available"
        echo "Start monitoring with: systemctl --user start gpd-hardware-monitor"
        exit 1
    fi

    source /tmp/gpd-hardware-status

    echo "=== GPD Pocket 3 Hardware Status ==="
    echo "Last Check: $LAST_CHECK"
    echo ""

    echo "Temperatures:"
    if [[ -n "$CPU_TEMP" ]]; then
        echo "  CPU: ''${CPU_TEMP}Â°C"
    else
        echo "  CPU: Not available"
    fi

    if [[ -n "$GPU_TEMP" ]]; then
        echo "  GPU: ''${GPU_TEMP}Â°C"
    else
        echo "  GPU: Not available"
    fi
    echo ""

    echo "Fan:"
    if [[ -n "$FAN_SPEED" ]]; then
        echo "  Speed: ''${FAN_SPEED} RPM"
    else
        echo "  Speed: Not available"
    fi
    echo ""

    echo "Battery:"
    if [[ -n "$BATTERY_LEVEL" ]]; then
        echo "  Level: ''${BATTERY_LEVEL}%"
        echo "  Status: ''${BATTERY_STATUS:-Unknown}"
        if [[ -n "$BATTERY_HEALTH" ]]; then
            echo "  Health: $BATTERY_HEALTH"
        fi
    else
        echo "  Status: Not available"
    fi
    echo ""

    echo "Alerts:"
    local alert_count=$(${pkgs.gnugrep}/bin/grep -c "WARN\|CRITICAL" "${cfg.logging.logFile}" 2>/dev/null || echo "0")
    echo "  Recent alerts: $alert_count (check ${cfg.logging.logFile})"
  '';

in {
  options.custom.system.hardware.monitoring = {
    enable = mkEnableOption "GPD Pocket 3 hardware monitoring";

    checkInterval = mkOption {
      type = types.int;
      default = 30;
      description = "Monitoring check interval in seconds";
    };

    alerts = {
      temperatureHigh = mkOption {
        type = types.int;
        default = 80;
        description = "High temperature alert threshold in Celsius";
      };

      batteryLow = mkOption {
        type = types.int;
        default = 15;
        description = "Low battery alert threshold in percentage";
      };

      method = mkOption {
        type = types.enum [ "notify" "systemd" "log" "all" ];
        default = "all";
        description = "Alert notification method";
      };
    };

    logging = {
      enable = mkOption {
        type = types.bool;
        default = true;
        description = "Enable logging of monitoring data";
      };

      logFile = mkOption {
        type = types.str;
        default = "/var/log/gpd-hardware-monitor.log";
        description = "Path to log file";
      };

      level = mkOption {
        type = types.enum [ "debug" "info" "warn" "error" ];
        default = "info";
        description = "Logging level";
      };
    };
  };

  config = mkIf cfg.enable {
    # Required packages
    environment.systemPackages = with pkgs; [
      lm_sensors
      smartmontools
      acpi
      libnotify
      monitor-script
      status-script
    ];

    # Enable hardware sensors
    hardware.sensor.iio.enable = true;

    # Kernel modules for sensors
    boot.kernelModules = [ "coretemp" "i915" ];

    # Initialize lm-sensors
    environment.etc."sensors.conf" = {
      text = ''
        # Sensors configuration for GPD Pocket 3

        chip "coretemp-*"
          label temp1 "CPU Temperature"

        chip "i915-*"
          label temp1 "GPU Temperature"
      '';
    };

    # User systemd service for hardware monitoring
    systemd.user.services.gpd-hardware-monitor = {
      description = "GPD Pocket 3 Hardware Monitor";
      wantedBy = [ "default.target" ];
      after = [ "graphical-session.target" ];

      serviceConfig = {
        Type = "simple";
        ExecStart = "${monitor-script}/bin/gpd-hardware-monitor monitor";
        ExecReload = "${pkgs.coreutils}/bin/kill -HUP $MAINPID";
        Restart = "always";
        RestartSec = "10";

        # Security settings
        NoNewPrivileges = true;
        PrivateTmp = true;
        ProtectSystem = "strict";
        ProtectHome = true;
        ReadWritePaths = [
          "/tmp"
          (dirOf cfg.logging.logFile)
        ];

        # Resource limits
        MemoryMax = "50M";
        CPUQuota = "10%";
      };

      environment = {
        PATH = mkDefault "/run/current-system/sw/bin";
      };
    };

    # System timer for periodic health checks
    systemd.timers.gpd-hardware-health-check = mkIf cfg.logging.enable {
      description = "GPD Pocket 3 Hardware Health Check Timer";
      wantedBy = [ "timers.target" ];

      timerConfig = {
        OnBootSec = "5m";
        OnUnitActiveSec = "1h";
        Unit = "gpd-hardware-health-check.service";
      };
    };

    systemd.services.gpd-hardware-health-check = mkIf cfg.logging.enable {
      description = "GPD Pocket 3 Hardware Health Check";

      serviceConfig = {
        Type = "oneshot";
        ExecStart = "${monitor-script}/bin/gpd-hardware-monitor test";
        User = "root";

        # Security settings
        NoNewPrivileges = true;
        PrivateTmp = true;
        ProtectSystem = "strict";
        ReadWritePaths = [
          "/tmp"
          (dirOf cfg.logging.logFile)
        ];
      };

      environment = {
        PATH = mkDefault "/run/current-system/sw/bin";
      };
    };

    # Log rotation
    services.logrotate.settings."${cfg.logging.logFile}" = mkIf cfg.logging.enable {
      frequency = "weekly";
      rotate = 4;
      compress = true;
      delaycompress = true;
      missingok = true;
      notifempty = true;
      create = "644 root root";
      postrotate = ''
        systemctl reload gpd-hardware-monitor || true
      '';
    };

    # Create log directory
    systemd.tmpfiles.rules = mkIf cfg.logging.enable [
      "d ${dirOf cfg.logging.logFile} 0755 root root -"
      "f ${cfg.logging.logFile} 0644 root root -"
    ];

    # Shell aliases for convenience
    environment.shellAliases = {
      hwmon = "${status-script}/bin/gpd-hardware-status";
      hwmon-log = "tail -f ${cfg.logging.logFile}";
      hwmon-test = "${monitor-script}/bin/gpd-hardware-monitor test";
    };
  };
}

