    10â†’    LOCK_FILE="/tmp/rotation-lock-state"
    11â†’    
    12â†’    # Toggle lock state
    13â†’    if [ -f "$LOCK_FILE" ] && [ "$(cat $LOCK_FILE)" = "locked" ]; then
    14â†’        echo "unlocked" > "$LOCK_FILE"
    15â†’        echo "Rotation unlocked"
    16â†’        # Signal waybar to update
    17â†’        pkill -RTMIN+20 waybar 2>/dev/null || true
    18â†’    else
    19â†’        echo "locked" > "$LOCK_FILE"
    20â†’        echo "Rotation locked"
    21â†’        # Signal waybar to update
    22â†’        pkill -RTMIN+20 waybar 2>/dev/null || true
    23â†’    fi
    24â†’  '';
