    26â†’  # Rotation lock status script
    27â†’  rotation-lock-status = pkgs.writeShellScriptBin "rotation-lock-status" ''
    28â†’    LOCK_FILE="/tmp/rotation-lock-state"
    29â†’    
    30â†’    if [ -f "$LOCK_FILE" ] && [ "$(cat $LOCK_FILE)" = "locked" ]; then
