    41â†’  config = lib.mkIf cfg.enable {
    42â†’    # Convert theme names to their corresponding packages, filtering out missing ones
    43â†’    home.packages = lib.filter (p: p != null) (map findThemeByName availableThemes);
    44â†’
    45â†’    # walks through the themes and creates symlinks in the hyde themes directory
    46â†’    home.file =
    47â†’      let
