    57â†’        map (theme: {
    58â†’          ".config/hyde/themes/${theme.name}" = {
    59â†’            source = "${theme.pkg}/share/hyde/themes/${theme.name}";
    60â†’            force = true;
    61â†’            recursive = true;
    62â†’            mutable = true;
    63â†’          };
    64â†’        }) themesList
