    95â†’      LOG_FILE="$HOME/.local/state/hyde/theme-switch.log"
    96â†’      mkdir -p $HOME/.local/state/hyde
    97â†’      # Clear the log file before writing
    98â†’      : > "$LOG_FILE"
    99â†’      chmod 644 $LOG_FILE
   100â†’
   101â†’      echo "Setting theme to ${cfg.active}..." | tee -a "$LOG_FILE"
   102â†’
