let
  cfg = config.custom.system.hardware.penInput;
in
{
  options.custom.system.hardware.penInput = {
    enable = lib.mkOption {
      type = lib.types.bool;
      default = false;
      description = "Enable GPD Pocket 3 pen/stylus input configuration";
    };

    device = lib.mkOption {
      type = lib.types.str;
      default = "GXTP7380:00 27C6:0113 Stylus";
      description = "Name of the pen/stylus device";
    };

    transformMatrix = lib.mkOption {
      type = lib.types.str;
      default = "0 -1 1 1 0 0 0 0 1";
      description = ''
        Libinput calibration matrix for pen input.
        Default is for 270-degree rotation (transform 3).
        Matrix format: [m11 m12 m13 m21 m22 m23 m31 m32 m33]
      '';
    };
  };

  config = lib.mkIf cfg.enable {
    # Configure pen input transformation via udev rules
    services.udev.extraRules = ''
      # GPD Pocket 3 Pen/Stylus input transformation
      # Apply rotation matrix to match display orientation (transform 3 = 270 degrees)
      ACTION=="add|change", KERNEL=="event[0-9]*", ATTRS{name}=="${cfg.device}", ENV{LIBINPUT_CALIBRATION_MATRIX}="${cfg.transformMatrix}"

      # Also apply to the base touch device for consistency
      ACTION=="add|change", KERNEL=="event[0-9]*", ATTRS{name}=="GXTP7380:00 27C6:0113", ENV{LIBINPUT_CALIBRATION_MATRIX}="${cfg.transformMatrix}"
    '';

    # Ensure libinput is available for debugging
    environment.systemPackages = with pkgs; [
      libinput
    ];
  };
}

