     5â†’let
     6â†’  cfg = config.custom.system.hardware.thermal;
     7â†’
     8â†’  # Thermal monitoring script for GPD Pocket 3
     9â†’  thermal-monitor-script = pkgs.writeShellScriptBin "thermal-monitor-gpd" ''
    10â†’    #!/bin/bash
    11â†’
    12â†’    # Thermal monitoring and management for GPD Pocket 3
    13â†’    # Provides emergency protection and thermal event logging
    14â†’
    15â†’    TEMP_FILE="/sys/class/thermal/thermal_zone0/temp"
    16â†’    EMERGENCY_TEMP=${toString (cfg.emergencyShutdownTemp * 1000)}
    17â†’    THROTTLE_TEMP=${toString (cfg.throttleTemp * 1000)}
    18â†’    CRITICAL_TEMP=${toString (cfg.criticalTemp * 1000)}
    19â†’    LOG_FILE="/var/log/thermal-monitor.log"
    20â†’    PID_FILE="/var/run/thermal-monitor.pid"
    21â†’
    22â†’    # Ensure log directory exists
    23â†’    mkdir -p "$(dirname "$LOG_FILE")"
    24â†’
    25â†’    # Create PID file
    26â†’    echo $$ > "$PID_FILE"
    27â†’
    28â†’    log_event() {
    29â†’        local level="$1"
    30â†’        local message="$2"
    31â†’        local timestamp=$(${pkgs.coreutils}/bin/date '+%Y-%m-%d %H:%M:%S')
    32â†’        echo "[$timestamp] [$level] $message" | ${pkgs.coreutils}/bin/tee -a "$LOG_FILE"
    33â†’
    34â†’        # Also log to journal
    35â†’        ${pkgs.systemd}/bin/systemd-cat -t thermal-monitor -p "$level" echo "$message"
    36â†’    }
    37â†’
    38â†’    get_cpu_temp() {
    39â†’        if [ -f "$TEMP_FILE" ]; then
    40â†’            cat "$TEMP_FILE"
    41â†’        else
    42â†’            echo "0"
    43â†’        fi
    44â†’    }
    45â†’
    46â†’    get_all_temps() {
    47â†’        local temps=""
    48â†’        for zone in /sys/class/thermal/thermal_zone*/temp; do
    49â†’            if [ -f "$zone" ]; then
    50â†’                local temp=$(cat "$zone")
    51â†’                local zone_name=$(basename "$(dirname "$zone")")
    52â†’                temps="$temps $zone_name:$(($temp / 1000))Â°C"
    53â†’            fi
    54â†’        done
    55â†’        echo "$temps"
    56â†’    }
    57â†’
    58â†’    emergency_shutdown() {
    59â†’        log_event "emerg" "EMERGENCY: CPU temperature reached emergency threshold! Initiating immediate shutdown."
    60â†’        log_event "emerg" "Current temperatures: $(get_all_temps)"
    61â†’
    62â†’        # Force immediate shutdown to prevent hardware damage
    63â†’        ${pkgs.systemd}/bin/systemctl poweroff --force
    64â†’    }
    65â†’
    66â†’    critical_throttle() {
    67â†’        log_event "crit" "CRITICAL: CPU temperature exceeded critical threshold. Applying emergency throttling."
    68â†’        log_event "crit" "Current temperatures: $(get_all_temps)"
    69â†’
    70â†’        # Set CPU to minimum frequency
    71â†’        for cpu in /sys/devices/system/cpu/cpu*/cpufreq/scaling_max_freq; do
    72â†’            if [ -f "$cpu" ]; then
    73â†’                local min_freq=$(cat "$(dirname "$cpu")/scaling_min_freq")
    74â†’                echo "$min_freq" > "$cpu" 2>/dev/null || true
    75â†’            fi
    76â†’        done
    77â†’
    78â†’        # Set CPU governor to powersave
    79â†’        for cpu in /sys/devices/system/cpu/cpu*/cpufreq/scaling_governor; do
    80â†’            if [ -f "$cpu" ]; then
    81â†’                echo "powersave" > "$cpu" 2>/dev/null || true
    82â†’            fi
    83â†’        done
    84â†’    }
    85â†’
    86â†’    apply_throttle() {
    87â†’        log_event "warning" "CPU temperature exceeded throttle threshold. Applying thermal throttling."
    88â†’        log_event "info" "Current temperatures: $(get_all_temps)"
    89â†’
    90â†’        # Reduce CPU frequency to 75% of maximum
    91â†’        for cpu in /sys/devices/system/cpu/cpu*/cpufreq/scaling_max_freq; do
    92â†’            if [ -f "$cpu" ]; then
    93â†’                local max_freq=$(cat "$(dirname "$cpu")/cpufreq_max_freq" 2>/dev/null || cat "$(dirname "$cpu")/scaling_max_freq")
    94â†’                local throttled_freq=$(($max_freq * 3 / 4))
    95â†’                echo "$throttled_freq" > "$cpu" 2>/dev/null || true
    96â†’            fi
    97â†’        done
    98â†’
    99â†’        # Set CPU governor to conservative
   100â†’        for cpu in /sys/devices/system/cpu/cpu*/cpufreq/scaling_governor; do
   101â†’            if [ -f "$cpu" ]; then
   102â†’                echo "conservative" > "$cpu" 2>/dev/null || true
   103â†’            fi
   104â†’        done
   105â†’    }
   106â†’
   107â†’    restore_performance() {
   108â†’        log_event "info" "Temperature normalized. Restoring performance settings."
   109â†’
   110â†’        # Restore maximum CPU frequency
   111â†’        for cpu in /sys/devices/system/cpu/cpu*/cpufreq/scaling_max_freq; do
   112â†’            if [ -f "$cpu" ]; then
   113â†’                local max_freq=$(cat "$(dirname "$cpu")/cpufreq_max_freq" 2>/dev/null || echo "999999999")
   114â†’                echo "$max_freq" > "$cpu" 2>/dev/null || true
   115â†’            fi
   116â†’        done
   117â†’
   118â†’        # Restore CPU governor based on configuration
   119â†’        for cpu in /sys/devices/system/cpu/cpu*/cpufreq/scaling_governor; do
   120â†’            if [ -f "$cpu" ]; then
   121â†’                echo "${cfg.normalGovernor}" > "$cpu" 2>/dev/null || true
   122â†’            fi
   123â†’        done
   124â†’    }
   125â†’
   126â†’    cleanup() {
   127â†’        log_event "info" "Thermal monitor stopped"
   128â†’        rm -f "$PID_FILE"
   129â†’        exit 0
   130â†’    }
   131â†’
   132â†’    # Set up signal handlers
   133â†’    trap cleanup EXIT INT TERM
   134â†’
   135â†’    log_event "info" "Starting thermal monitor for GPD Pocket 3"
   136â†’    log_event "info" "Thresholds: Throttle=${toString cfg.throttleTemp}Â°C, Critical=${toString cfg.criticalTemp}Â°C, Emergency=${toString cfg.emergencyShutdownTemp}Â°C"
   137â†’
   138â†’    # State tracking
   139â†’    throttle_active=false
   140â†’    critical_active=false
   141â†’    last_temp=0
   142â†’    stable_count=0
   143â†’
   144â†’    while true; do
   145â†’        current_temp=$(get_cpu_temp)
   146â†’        current_temp_c=$(($current_temp / 1000))
   147â†’
